// Função Node-RED para converter payload em porcentagem
function toPercentage(payload) {
    if (payload >= 0 && payload < 1000) {
        let strPayload = payload.toString();
        let integerPart = strPayload.slice(0, -1); // Parte inteira da porcentagem
        let decimalPart = strPayload.slice(-1); // Último dígito

        // Se a parte inteira estiver vazia, coloque 0
        if (integerPart === '') {
            integerPart = '0';
        }

        return parseFloat(integerPart + '.' + decimalPart);
    } else if (payload >= 1000) {
        return 100.0; // Automaticamente define como 100%
    } else {
        throw new Error('Payload fora do intervalo esperado (menor que 0)');
    }
}

